if( ( typeof utag !== 'undefined' ) && ( typeof utag.data !== 'undefined' ) ) { utag.data['cp.nukt_sp_consent'] = window.nonIabPurposeConsents; }

// clear the queue of Tealium calls, if needed
if( ( typeof window.nukt_cmp !== 'undefined' ) && ( typeof window.nukt_cmp.flushQueue !== 'undefined' ) ) {
  window.nukt_cmp.flushQueue();
}



// code to handle the cookie V2 for new Tealium integration
if( ( typeof _sp_ !== 'undefined' ) && ( typeof _sp_.getCustomVendorConsents === 'function' ) ) {

  _sp_.getCustomVendorConsents( function( v, success ) {
    if( success ) {
      // consent information is available
	  // read the data structure and set the flags
	  var f = '';
	  if( typeof v.consentedPurposes !== 'undefined' &&
	      v.consentedPurposes.constructor === Array &&
		  v.consentedPurposes.length > 0 ) {
	    for( var i = 0; i < v.consentedPurposes.length; i++ ) {

          switch( v.consentedPurposes[i]._id.toLowerCase() ) {
		    case '5ff72d5ebdf037283e40a4c2': f += 'A'; break; 
		    case '5ff72d5ebdf037283e40a4c7': f += 'B'; break;
			case '6014443f87b0803b0f3e38c1': f += 'C'; break;
			case '6014443f87b0803b0f3e38c6': f += 'D'; break;
			case '6014443f87b0803b0f3e38cb': f += 'E'; break;
			case '6014443f87b0803b0f3e38d0': f += 'F'; break;
			case '6014443f87b0803b0f3e38d5': f += 'G'; break;
			case '6014443f87b0803b0f3e38da': f += 'H'; break;
			case '6014443f87b0803b0f3e38df': f += 'I'; break;
			case '5ff72d5ebdf037283e40a4bd': f += 'J'; break;
		  }

		}
	  }
	  f = f + '|';
	  if( typeof v.legIntPurposes !== 'undefined' &&
	      v.legIntPurposes.constructor === Array &&
		  v.legIntPurposes.length > 0 ) {
	    for(var j = 0; j < v.legIntPurposes.length; j++ ) {
			
          switch( v.legIntPurposes[j]._id.toLowerCase() ) {
		    case '5ff72d5ebdf037283e40a4c2': f += 'N'; break; 
		    case '5ff72d5ebdf037283e40a4c7': f += 'O'; break;
			case '6014443f87b0803b0f3e38c1': f += 'P'; break;
			case '6014443f87b0803b0f3e38c6': f += 'Q'; break;
			case '6014443f87b0803b0f3e38cb': f += 'R'; break;
			case '6014443f87b0803b0f3e38d0': f += 'S'; break;
			case '6014443f87b0803b0f3e38d5': f += 'T'; break;
			case '6014443f87b0803b0f3e38da': f += 'U'; break;
			case '6014443f87b0803b0f3e38df': f += 'V'; break;
		  }
			
		}
      }
	  
	  // update the cookie V2 with the overall consent information
      now = new Date();
      time = now.getTime();
      time += 94608000000; // three years later
      now.setTime(time);
      // drop the cookie
      document.cookie = 'nukt_sp_consent_v2=' + encodeURIComponent(f) + ';expires=' + now.toGMTString() + ';path=/';
	  
	  // update Tealium data layer, if existing
      if( typeof utag_data !== 'undefined' ) {
        utag_data['cp.nukt_sp_consent_v2'] = f;
      }
      if( typeof utag !== 'undefined' && typeof utag.data !== 'undefined' ) {
        utag.data['cp.nukt_sp_consent_v2'] = f;
      }
      
	  // clear the queue of Tealium calls, if needed
      if( ( typeof window.nukt_cmp !== 'undefined' ) && ( typeof window.nukt_cmp.flushQueueV2 !== 'undefined' ) ) {
        window.nukt_cmp.flushQueueV2();
      }
    }
  });
}
